import React from "react";

const Profile = () => {
  const user = {
    username: "JohnDoe",
    email: "john.doe@example.com",
    password: "************", // Masked password for security
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="space-y-4">
        <div>
          <h1 className="text-2xl font-semibold text-gray-800">Username</h1>
          <p className="text-gray-600">{user.username}</p>
        </div>

        <div>
          <h1 className="text-2xl font-semibold text-gray-800">Email</h1>
          <p className="text-gray-600">{user.email}</p>
        </div>

        <div>
          <h1 className="text-2xl font-semibold text-gray-800">Password</h1>
          <p className="text-gray-600">{user.password}</p>
        </div>
      </div>
      <div className="mt-6">
        <button className="w-full py-2 px-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300">
          Edit Profile
        </button>
      </div>
    </div>
  );
};

export default Profile;
