import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/user/profile", {
          method: "GET",
          credentials: "include", // Ensures session data (cookies) are sent
          headers: {
            "Content-Type": "application/json",
          },
        });

        const data = await res.json();
        if (data.success) {
          setUser(data.user);
        } else {
          setError("Failed to load profile");
        }
      } catch (error) {
        console.error("Error:", error);
        setError("Something went wrong. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  if (loading) {
    return <div className="text-center text-gray-600">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500">{error}</div>;
  }

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r">
      <div className="max-w-lg w-full p-8 bg-white rounded-xl shadow-2xl">
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            User Profile
          </h1>
          <p className="text-lg text-gray-500">
            View and update your details below
          </p>
        </div>

        <div className="space-y-6">
          {/* Username */}
          <div className="flex justify-between items-center p-4 rounded-lg bg-gray-50 shadow-md">
            <h2 className="text-xl font-semibold text-gray-800">Username</h2>
            <p className="text-gray-600">{user?.username}</p>
          </div>

          {/* Email */}
          <div className="flex justify-between items-center p-4 rounded-lg bg-gray-50 shadow-md">
            <h2 className="text-xl font-semibold text-gray-800">Email</h2>
            <p className="text-gray-600">{user?.email}</p>
          </div>
        </div>

        <div className="mt-8">
          <button
            onClick={() => navigate("/editProfile")}
            className="w-full py-3 px-6 bg-gradient-to-r from-blue-500 to-blue-700 text-white font-semibold rounded-lg hover:from-blue-600 hover:to-blue-800 transition duration-300"
          >
            Edit Profile
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
