import { useEffect, useRef, useState } from "react";
import "./chatApp.css";
import Img1 from "../assets/img1.jpg";
import Input from "../components/Input/Input";
import { io } from "socket.io-client";

function ChatApp() {
  //fetch the login user details from the database
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/user/profile", {
          method: "get",
          credentials: "include",
        });

        const data = await res.json();
        console.log("Fetched Data:", data);
        if (data.success) {
          setUser(data.user);
        } else {
          setError("Failed to fetch user details.");
        }
      } catch (error) {
        console.error("Error fetching user details:", error);
        setError("Something went wrong. Please try again.");
      }
    };

    fetchProfile();
  }, []);
  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("user:detail"))
  );
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({ messages: [] });
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const messageRef = useRef(null);

  // Initialize socket connection
  useEffect(() => {
    const socketInstance = io("http://localhost:5000");
    setSocket(socketInstance);

    return () => {
      socketInstance.disconnect();
    };
  }, []);

  // Handle socket communication for new messages
  useEffect(() => {
    if (socket && user?.id) {
      socket.emit("addUser", user?.id);

      socket.on("getMessage", (data) => {
        setMessages((prevMessages) => ({
          ...prevMessages,
          messages: [
            ...prevMessages.messages,
            { user: data.user, message: data.message },
          ],
        }));
      });
    }
  }, [socket, user?.id]);

  // Scroll to the latest message
  useEffect(() => {
    if (messageRef.current) {
      messageRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages?.messages]);

  // Fetch conversations
  useEffect(() => {
    const fetchConversations = async () => {
      if (user?.id) {
        const res = await fetch(
          `http://localhost:5000/api/conversations/${user.id}`
        );
        const data = await res.json();
        setConversations(data);
      }
    };
    fetchConversations();
  }, [user?.id]);

  // Fetch online users
  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:5000/api/users`);
      const data = await res.json();
      setUsers(data);
    };
    fetchUsers();
  }, []);

  // Fetch messages for a selected conversation
  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(
      `http://localhost:5000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`
    );
    const data = await res.json();
    setMessages({ messages: data, receiver, conversationId });
  };

  // Send message to the receiver
  const sendMessage = async () => {
    if (message.trim()) {
      setMessage("");
      socket?.emit("sendMessage", {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId,
      });

      const res = await fetch(`http://localhost:5000/api/message`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          conversationId: messages?.conversationId,
          senderId: user?.id,
          message,
          receiverId: messages?.receiver?.receiverId,
        }),
      });
      const resData = await res.json();
      setMessages((prevMessages) => ({
        ...prevMessages,
        messages: [...prevMessages.messages, { user: user, message }],
      }));
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Sidebar: Conversations */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <hr />
        <div className="mx-14 mt-10">
          <div className="text-primary text-lg">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user }) => (
                <div
                  key={conversationId}
                  className="flex items-center py-8 border-b border-b-gray-300"
                >
                  <div
                    className="cursor-pointer flex items-center"
                    onClick={() => fetchMessages(conversationId, user)}
                  >
                    <img
                      src={Img1}
                      className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                    />
                    <div className="ml-6">
                      <h3 className="text-lg font-semibold">
                        {user?.fullName}
                      </h3>
                      <p className="text-sm font-light text-gray-600">
                        {user?.email}
                      </p>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No Conversations
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Chat Window: Messages */}
      <div className="w-[50%] h-screen bg-white flex flex-col items-center">
        {messages?.receiver?.fullName && (
          <div className="w-[75%] bg-secondary h-[80px] my-14 rounded-full flex items-center px-14 py-2">
            <img src={Img1} width={60} height={60} className="rounded-full" />
            <div className="ml-6 mr-auto">
              <h3 className="text-lg">{messages?.receiver?.fullName}</h3>
              <p className="text-sm font-light text-gray-600">
                {messages?.receiver?.email}
              </p>
            </div>
          </div>
        )}

        {/* Messages Area */}
        <div className="h-[75%] w-full overflow-scroll shadow-sm">
          <div className="p-14">
            {messages?.messages?.length > 0 ? (
              messages.messages.map(({ message, user: { id } = {} }, index) => (
                <div
                  key={index}
                  className={`max-w-[40%] rounded-b-xl p-4 mb-6 ${
                    id === user?.id
                      ? "bg-primary text-white rounded-tl-xl ml-auto"
                      : "bg-secondary rounded-tr-xl"
                  }`}
                >
                  {message}
                </div>
              ))
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No Messages or No Conversation Selected
              </div>
            )}
          </div>
        </div>

        {/* Message Input */}
        {messages?.receiver?.fullName && (
          <div className="p-14 w-full flex items-center">
            <Input
              placeholder="Type a message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="w-[75%]"
              inputClassName="p-4 border-0 shadow-md rounded-full bg-light focus:ring-0 focus:border-0 outline-none"
            />
            <div
              className={`ml-4 p-2 cursor-pointer bg-light rounded-full ${
                !message && "pointer-events-none"
              }`}
              onClick={sendMessage}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="icon icon-tabler icon-tabler-send"
                width="30"
                height="30"
                viewBox="0 0 24 24"
                stroke-width="1.5"
                stroke="#2c3e50"
                fill="none"
                stroke-linecap="round"
                stroke-linejoin="round"
              >
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <line x1="10" y1="14" x2="21" y2="3" />
                <path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
              </svg>
            </div>
          </div>
        )}
      </div>

      {/* Right Sidebar: Active Users */}
      <div className="w-[25%] h-screen bg-light px-8 py-16 overflow-scroll">
        <div className="text-primary text-lg">People</div>
        <div>
          {users.length > 0 ? (
            users.map(({ userId, user }) => (
              <div
                key={userId}
                className="flex items-center py-8 border-b border-b-gray-300"
              >
                <div
                  className="cursor-pointer flex items-center"
                  onClick={() => fetchMessages("new", user)}
                >
                  <img
                    src={Img1}
                    className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                  />
                  <div className="ml-6">
                    <h3 className="text-lg font-semibold">{user?.fullName}</h3>
                    <p className="text-sm font-light text-gray-600">
                      {user?.email}
                    </p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="text-center text-lg font-semibold mt-24">
              No Active Users
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ChatApp;
