import React, { useEffect, useState } from "react";
import "./profile.css";
import { useNavigate } from "react-router-dom";

function Profile() {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
  });

  const navigate = useNavigate();

  // Fetch user data when component mounts
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/user/profile", {
          method: "GET",
          headers: { "Content-Type": "application/json" },
        });
        const data = await res.json();
        if (res.ok) {
          setUserData(data); // Assuming data contains the user details
        } else {
          alert(data.message || "Failed to fetch user data.");
        }
      } catch (error) {
        console.error("Error:", error);
        alert("Something went wrong while fetching user data.");
      }
    };
    fetchUserData();
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  return (
    <div>
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="bg-white p-8 rounded-lg shadow-lg w-96">
          <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
            User Profile
          </h2>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Full Name
              </label>
              <input
                type="text"
                name="name"
                value={userData.name}
                readOnly
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={userData.email}
                readOnly
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
            <button
              onClick={() => navigate("/editProfile")}
              className="w-full py-2 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              Edit Account
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
